import React,{ Component} from 'react';
import './../App.css';
import Header from './Header';
import Search from './Search';
import TableData from './TableData';
import AddUser from './AddUser';
import userData from './data.json'


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showForm : false,
      data : userData,
      searchedText : ''
    };
  }

  getSeachedText = (text)=> {
    this.setState({
      searchedText : text
    });
  }

  changeAppShowForm = () => { 
      this.setState({
        showForm : !this.state.showForm
      });
   }

  render() {
    SearchedDataArr = []
    if (this.setState.searchedText){
      this.state.userData.map( (value,key) =>{
          if( value.name.indexOf(this.state.searchedText) !== -1){
            SearchedDataArr.push(value)
          }
      })
    }
    print
    return (
      <div>
          <Header/>
          <div className="SearchForm">
            <div className="container">
                <div className="row">
                    <Search  showFormFromApp = {() => this.changeAppShowForm()}
                       getSeachedText = {(text) => this.getSeachedText(text)}/>
                    <TableData data = {this.state.data} />
                    <AddUser showForm = { this.state.showForm }/>
                </div>
            </div>
          </div>
      </div>
    );
  }

}

export default App;
