import React, { Component } from 'react';

class AddUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            status : false
        }
    }

    isChange  = () => {
             
    }

    showForm = ()=> {
        if(this.props.showForm === true){
            return (
                <div className="col">
                        <div className="card border-primary mb-3 mt-2" style={{maxWidth: '18rem'}}>                        
                        <div className="card-header">Add new</div>
                        <div className="card-body text-primary">
                        <div className="form-group">                        
                        <input name = 'name' type="text"  className="form-control" placeholder="username" aria-describedby="helpId" />
                        </div>
                        <div className="form-group">                        
                        <input type="text"  className="form-control" placeholder="phone" aria-describedby="helpId" />
                        </div>
                        <div className="form-group">                        
                        <input type="text"  className="form-control" placeholder="email" aria-describedby="helpId" />
                        </div>
                        <div className="form-group">                        
                        <select className="custom-select" required>
                            <option >Choose Authoriztion</option>
                            <option value={1}>Amin</option>
                            <option value={2}>Moderator</option>
                            <option value={3}>Normal</option>
                        </select>                      
                        </div>
                        <div className="form-group">
                        <div className="btn btn-block btn-primary">Add new</div>
                        </div>
                    </div>
                    </div>
                </div>
                )
        }else{
            return false
        }        
    }


    render() {
        return (
                    <div>                    
                        { this.showForm()}
                    </div>
                
        );
    }
}

export default AddUser;
