import React,{ Component} from 'react';
import './../App.css';
import Header from './Header';
import Search from './Search';
import TableData from './TableData';
import AddUser from './AddUser';
import JsonUserData from './data.json'


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showForm : false,
      data : JsonUserData,
      searchedText : ''
    };
  }

  getNewUser  = (name,phone,permission) => {
      var item  = {}
      item.id = 7
      item.name = name
      item.phone = phone
      item.permission = permission
      console.log('item: ',item);
      var items = this.state.data
      items.push(item)
      this.setState({
        data : items
      })
      console.log(this.state.data);
    }


  getSeachedText = (text)=> {
    this.setState({
      searchedText : text
    });
  }

  changeAppShowForm = () => { 
      this.setState({
        showForm : !this.state.showForm
      });
   }

  render() {
    var SearchedDataArr = []
    this.state.data.map( (value,key) =>{
        if( value.name.indexOf(this.state.searchedText) !== -1){
          SearchedDataArr.push(value)
        }
        return true
    }) 
    // console.log(SearchedDataArr);
    return (
      <div>
          <Header/>
          <div className="SearchForm">
            <div className="container">
                <div className="row">
                    <Search  showFormFromApp = {() => this.changeAppShowForm()}
                       getSeachedText = {(text) => this.getSeachedText(text)}/>
                    <TableData data = {SearchedDataArr} />
                    <AddUser showForm = { this.state.showForm } getNewUser = { (name,phone,permission) => this.getNewUser(name,phone,permission)}/>
                </div>
            </div>
          </div>
      </div>
    );
  }

}

export default App;
